import React, { Component } from "react";

const BookList = ({books}) => (
    <div>
        {books.map(({ title, review }) => (
            <p> {title} {review} </p>
        ))}
    </div>
);

class SubmitBook extends Component {

    constructor(){
        super();
        this.state = {
            "books": [],
            material_id: null,
            start_data: null,
            end_data: null
        };

        this.handleChange = this.handleChange.bind(this)
    };
    
    handleChange = (e) => {
        this.setState({...this.state.request_data, [data.target]: data.value})
       }
       
               onSubmit = () => {
                 console.log(this.state.request_data)   // you should be able to see your form data
             }

    componentDidMount(){
        fetch("/api/getAllBooks")
            .then(response => response.json())
            .then(response => this.setState({ books: response}))
    }

    submit_task(data) {
        fetch("/api/tasks",
            {
                method: "POST",
                cache: "no-cache",
                headers:{
                    "content_type": "application/json"
                },
                body: JSON.stringify(data)
            })
            .then(response=> response.json())

    }

    render() {
        return (
            <div>
                <h2>Submit Job</h2>
                <form id="submit_job" onSubmit={this.onSubmit}>
                    <label>
                        Material ID:
                        <input type="text" name="material_id" onChange={this.handleChange}/>
                    </label>
                    <br/>
                    <label>
                        Transcode Profile:
                        <BookList books={this.state.books}/>
                    </label>
                    <br/>
                    <label>
                        Title:
                        <input type="text" name="title" onChange={this.handleChange}/>
                    </label>
                    <br/>
                    <label>
                        Review:
                        <input type="text" name="review" onChange={this.handleChange}/>
                    </label>
                    <br/>
                    <button type="submit">Submit</button>
                </form>
            </div>

        );
    }
}

export default SubmitBook;
