import React, { Component } from "react";

class SubmitBook extends Component {

    constructor(){
        super();
        this.state = {
            "books": [],
            title: null,
            review: null
        };
        this.handleChange = this.handleChange.bind(this)
    };
    
    handleChange = (e) => {
        console.log(e.target.name, e.target.value)
        this.setState({...this.state.request_data, [e.target]: e.target.value})
    }
       
    onSubmit = () => {
        this.submit_task(this.state.request_data)   // you should be able to see your form data
    }

    componentDidMount(){
    }

    submit_task(data) {
        fetch("/api/addBooks",
            {
                method: "POST",
                cache: "no-cache",
                headers:{
                    "content_type": "application/json"
                },
                body: JSON.stringify(data)
            })
            .then(response=> response.json())

    }

    render() {
        return (
            <div>
                <h2>Add a book.</h2>
                <form id="submit_book" onSubmit={this.onSubmit}>
                    <label>
                        Title:
                        <input type="text" name="title" onChange={this.handleChange}/>
                    </label>
                    <br/>
                    <label>
                        Review:
                        <input type="text" name="review" onChange={this.handleChange}/>
                    </label>
                    <br/>
                    <button type="submit">Submit</button>
                </form>
            </div>

        );
    }
}

export default SubmitBook;
